mapper.select:
SELECT u.nickname, u.avatar, c.user_id, c.id, c.content, c.create_time FROM b_comment c JOIN b_user_info u ON c.user_id = u.id WHERE
c.article_id = #{articleId}
c.article_id IS NULL
AND c.parent_id IS NULL ORDER BY c.id DESC LIMIT #{page},#{size}

SELECT parent_id as comment_id, count(1) AS reply_count FROM b_comment WHERE parent_id IN (
#{commentId}
) GROUP BY parent_id
